import React, { useState } from 'react';
import Select from 'react-select';

const options = [
    { label: 'Legal Opinion (Draft) ', value: 'LegalOpinionDraft' },
    { label: 'Legal Advice Final (Prosecution) ', value: 'LegalAdviceFinal(Prosecution)' },
    { label: 'Legal Advice Final (Non-Prosecution)', value: 'LegalAdviceFinalNonProsecution' },
    { label: 'Issuance Of Information (High Court) ', value: 'IssuanceOfInformation(High Court)' },
    { label: 'Arraignment', value: 'Arraignment' },
    { label: 'Legal Advice Dispatched (Prosecution)', value: 'LegalAdviceDispatched(Prosecution)' },
    { label: 'Legal Advice Dispatched (Non Prosecution) ', value: ' LegalAdviceDispatched(NonProsecution)' },
    { label: 'Legal Advice Dispatched (Prose & Non Prose)', value: ' LegalAdviceDispatched(Prose&Non Prose)' },
    { label: 'Charge Sheet', value: 'ChargeSheet' },
];

export default function Reports() {
    const [state, setState] = useState({
        selectedOption: null,
    });
    const handleChange = (selectedOption) => {
        setState({ selectedOption });
        // console.log(`Option selected:`, selectedOption);
    };
    const { selectedOption } = state;
    return (
        <>
            <div className="row mb-4 mt-15">
                <div className="form-group">
                    <label className="form-label">Select Categories</label>
                    <Select value={selectedOption} onChange={handleChange} options={options} isMulti />
                </div>
                <div className="form-group col-md-6">
                    <label className="form-label" for="inputAddress">
                        Select Date (From)
                    </label>
                    <input type="date" className="form-control" name="fromDate" id="fromDate" placeholder="" />
                </div>
                <div className="form-group col-md-6">
                    <label className="form-label" for="inputAddress">
                        Select Date (To)
                    </label>
                    <input type="date" className="form-control" name="toDate" id="toDate" placeholder="" />
                </div>
            </div>
            <button type="button" className="waves-effect waves-light btn mb-5 bg-gradient-primary">
                Generate
            </button>
        </>
    );
}
