import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import './menubox.css';

const SubMenu = styled.div`
    display: none;
`;

export default function MenuBox({ to = '/', active, icon, name }) {
    const [subMenuState, setSubMenuState] = useState(false);
    const subMenuStateHandler = () => setSubMenuState(!subMenuState);
    return (
        <li className={`list ${active && 'active'}`}>
            <b />
            <b />
            <Link to={to} onClick={subMenuStateHandler}>
                <span className="icon">{icon}</span>
                <span className="title">{name}</span>
            </Link>
            <SubMenu style={{ display: subMenuState ? 'block' : 'none' }}>
                <Link to={to}>
                    <span className="icon">{icon}</span>
                    <span className="title">{name}</span>
                </Link>
                <Link to={to}>
                    <span className="icon">{icon}</span>
                    <span className="title">{name}</span>
                </Link>
            </SubMenu>
        </li>
    );
}
