import React, { useState } from 'react';
import { xorBy } from 'lodash';
import { Select } from '@mevron/cross-core/components/Forms';
import styled from 'rn-css';
import { colors, Text } from '@mevron/cross-core/components/typo';

const GenerateButton = styled.TouchableOpacity`
    height: 40px;
    width: 200px;
    background-color: ${colors.defaultColor};
    margin-top: 10px;
    border-radius: 5px;
    justify-content: center;
    align-items: center;
`;

const GenerateButtonText = styled(Text)`
    color: #fff;
`;

export default function AdvancedSearch() {
    const [selectedTeams, setSelectedTeams] = useState([]);

    const K_OPTIONS = [
        {
            item: 'First Name or Last Name',
            id: 'name',
        },
        {
            item: 'File Name',
            id: 'fileName',
        },
        {
            item: 'Search by Court Charge Number',
            id: 'chargeNumber',
        },
        {
            item: 'Search by File Number',
            id: 'fileNumber',
        },
        {
            item: 'Search by Case Type',
            id: 'caseType',
        },
        {
            item: 'Search by Staffs Activities',
            id: 'staffActivities',
        },
        {
            item: 'Search by Case Endorsement',
            id: 'caseEndorsement',
        },
    ];
    function onChange() {
        return (val) => setSelectedTeams(val);
    }

    console.log(selectedTeams);

    return (
        <>
            <Select
                title="Select Categories"
                value={selectedTeams}
                options={K_OPTIONS}
                onChange={onChange()}
                hideInputFilter={false}
            />
            <GenerateButton>
                <GenerateButtonText>Search</GenerateButtonText>
            </GenerateButton>
        </>
    );
}
