import React from 'react';
import styled from 'styled-components';
import { colors } from '@mevron/cross-core/components/typo';

const SidebarMenuContainer = styled.div`
    position: relative;

    .menu-action-button {
        background: rgba(255, 255, 255, 0.7);
        border-radius: 24px;
        opacity: 0.8;
        height: 30px;
        width: 30px;
    }

    .navigation {
        height: 100vh;
        width: 70px;
        box-sizing: initial;
        border-radius: 10px;
        border-left: 5px solid #fff;
        background: #fff;
        transition: width 0.5s;
        overflow-x: hidden;
        position: relative;
        overflow: hidden;
    }

    .navigation:hover {
        overflow-y: auto;
    }

    .navigation.active {
        width: 270px;
    }

    .navigation ul {
        padding-top: 40px;
        width: 100%;
    }

    .navigation ul li {
        position: relative;
        list-style: none;
        width: 100%;
        border-top-left-radius: 20px;
        border-bottom-left-radius: 20px;
    }

    .navigation ul li.active {
        background: ${colors.backgroundColor};
    }

    .navigation ul li b:nth-child(1) {
        position: absolute;
        top: -20px;
        height: 20px;
        width: 100%;
        background: ${colors.backgroundColor};
        display: none;
    }

    .navigation ul li b:nth-child(1)::before {
        content: '';
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        border-bottom-right-radius: 20px;
        background: #fff;
    }

    .navigation ul li b:nth-child(2) {
        position: absolute;
        bottom: -20px;
        height: 20px;
        width: 100%;
        background: ${colors.backgroundColor};
        display: none;
    }

    .navigation ul li b:nth-child(2)::before {
        content: '';
        position: absolute;
        bottom: 0;
        left: 0;
        width: 100%;
        height: 100%;
        border-top-right-radius: 20px;
        background: #fff;
    }

    .navigation ul li.active b:nth-child(1),
    .navigation ul li.active b:nth-child(2) {
        display: block;
    }

    .navigation ul li a {
        position: relative;
        display: block;
        width: 100%;
        display: flex;
        text-decoration: none;
        color: ${colors.textColor};
    }

    .navigation ul li.active a {
        color: #333;
    }

    .navigation ul li a .icon {
        position: relative;
        display: block;
        min-width: 60px;
        height: 60px;
        line-height: 70px;
        text-align: center;
    }

    .navigation ul li a .icon ion-icon {
        font-size: 1.5rem;
    }

    .navigation ul li a .title {
        position: relative;
        display: block;
        padding-left: 10px;
        height: 60px;
        line-height: 60px;
        white-space: normal;
    }
    .toggle {
        border-radius: 0 50% 50% 0;
        box-shadow: -14px 5px 20px rgba(3, 27, 78, 0.1);
        width: 45px;
        height: 45px;
        overflow: hidden;
        text-align: center;
        align-items: center;
        display: flex;
        justify-content: center;
        position: absolute;
        right: -31px;
        top: 50%;
        background-color: #fff;
        cursor: pointer;
        z-index: 999;
    }
`;

export default function SidebarContainer({ children }) {
    return <SidebarMenuContainer>{children}</SidebarMenuContainer>;
}
