import React, { useState } from 'react';
import Select from 'react-select';

const options = [
    { label: 'Legal Opinion (Draft) ', value: 'LegalOpinionDraft' },
    { label: 'Legal Advice Final (Prosecution) ', value: 'LegalAdviceFinal(Prosecution)' },
    { label: 'Legal Advice Final (Non-Prosecution)', value: 'LegalAdviceFinalNonProsecution' },
    { label: 'Issuance Of Information (High Court) ', value: 'IssuanceOfInformation(High Court)' },
    { label: 'Arraignment', value: 'Arraignment' },
    { label: ' Legal Advice Dispatched (Prosecution)', value: 'LegalAdviceDispatched(Prosecution)' },
    { label: ' Legal Advice Dispatched (Non Prosecution) ', value: ' LegalAdviceDispatched(NonProsecution)' },
    { label: 'Legal Advice Final (Non-Prosecution)', value: 'LegalAdviceFinalNonProsecution' },
];

export default function Reports() {
    const [state, setState] = useState({
        selectedOption: null,
    });
    const handleChange = (selectedOption) => {
        setState({ selectedOption });
        // console.log(`Option selected:`, selectedOption);
    };
    const { selectedOption } = state;
    return <Select value={selectedOption} onChange={handleChange} options={options} isMulti />;
}
