import React, { useState } from 'react';
import { xorBy } from 'lodash';
import { Select, DatePicker, TextInput } from '@mevron/cross-core/components/Forms';
import styled from 'rn-css';
import { colors, Text } from '@mevron/cross-core/components/typo';

const FormRow = styled.View`
    flex-direction: row;
`;

const FormRowContainer = styled.View`
    flex: 1;
    margin: 5px;
`;

const DateRangeContainer = styled.View`
    flex-direction: row;
`;

const GenerateButton = styled.TouchableOpacity`
    height: 40px;
    width: 200px;
    background-color: ${colors.defaultColor};
    margin-top: 10px;
    border-radius: 5px;
    justify-content: center;
    align-items: center;
`;

const GenerateButtonText = styled(Text)`
    color: #fff;
`;

export default function BacklogCase() {
    const [selectedTeams, setSelectedTeams] = useState([]);
    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());

    const [gender, setGender] = useState([]);
    const genderList = [
        {
            item: 'Male',
            id: '1',
        },
        {
            item: 'Female',
            id: '2',
        },
    ];
    const onChangeGender = (val) => setGender(val);

    const [fileType, setFileType] = useState();
    const fileTypeList = [
        {
            item: 'Criminal',
            id: 'criminal',
        },
        {
            item: 'FHR',
            id: 'fhr',
        },
        {
            item: 'Plea Bargaining',
            id: 'pleaBargaining',
        },
    ];
    const onChangeFileType = (val) => setFileType(val);

    const K_OPTIONS = [
        {
            item: 'Legal Opinion (Draft) ',
            id: 'LegalOption',
        },
        {
            item: 'Legal Advice Final (Prosecution)',
            id: 'LegalAdvice',
        },
        {
            item: 'Legal Advice Final (Non-Prosecution) ',
            id: 'LegalAdviceFinal2',
        },
    ];
    function onMultiChange() {
        return (item) => setSelectedTeams(xorBy(selectedTeams, [item], 'id'));
    }

    return (
        <>
            <FormRow>
                <FormRowContainer>
                    <TextInput title="First Name" />
                </FormRowContainer>
                <FormRowContainer>
                    <TextInput title="Middile Name" />
                </FormRowContainer>
                <FormRowContainer>
                    <TextInput title="Last Name" />
                </FormRowContainer>
            </FormRow>
            <FormRow>
                <FormRowContainer>
                    <TextInput title="Phone Number" />
                </FormRowContainer>
                <FormRowContainer>
                    <TextInput title="Age" />
                </FormRowContainer>
                <FormRowContainer>
                    <Select
                        title="Gender"
                        options={genderList}
                        value={gender}
                        onChange={onChangeGender}
                        hideInputFilter={false}
                    />
                </FormRowContainer>
            </FormRow>

            <FormRow>
                <FormRowContainer>
                    <DatePicker
                        title="Date/Time (Case Received)"
                        selected={startDate}
                        onChange={(date) => setStartDate(date)}
                        dateFormat="MMMM d, yyyy h:mm aa"
                        showTimeSelect
                    />
                </FormRowContainer>
                <FormRowContainer>
                    <TextInput title="File Name" />
                </FormRowContainer>
                <FormRowContainer>
                    <Select
                        title="File Type"
                        options={fileTypeList}
                        value={fileType}
                        onChange={onChangeFileType}
                        hideInputFilter={false}
                    />
                </FormRowContainer>
            </FormRow>

            <Select
                title="Select Categories"
                options={K_OPTIONS}
                selectedValues={selectedTeams}
                onMultiSelect={onMultiChange()}
                onTapClose={onMultiChange()}
                isMulti
            />
            <GenerateButton>
                <GenerateButtonText>Generate</GenerateButtonText>
            </GenerateButton>
        </>
    );
}
