import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
    background: #ffffff;
    border-radius: 6px;
    box-shadow: rgba(34, 41, 47, 0.05) 0px 1px 2px 0px, rgba(34, 41, 47, 0.05) 0px 1px 4px 0px,
        rgba(34, 41, 47, 0.05) 0px 2px 8px 0px;
    transition: all 0.3s ease-in-out, background 0s, color 0s, border-color 0s;
`;

export default function Cards({ children, nopadding, style }) {
    return (
        <Container nopadding={nopadding} style={style}>
            {children}
        </Container>
    );
}
