import React, { useState } from 'react';

export default function Menu({ title, subTitle, menu }) {
    const [openMenu, setOpenMenu] = useState(false);
    const openMenuHandler = () => setOpenMenu(!openMenu);

    return (
        <>
            <li className="header">{title}</li>
            <li className={`treeview ${openMenu && 'active menu-open'}`} onClick={openMenuHandler}>
                <a href="#">
                    {subTitle.icon}
                    <span>{subTitle.name}</span>
                </a>
                <ul className="treeview-menu">
                    {menu.map((data, index) => {
                        return (
                            <li key={index}>
                                <a href="index.html">
                                    {data.icon}
                                    {data.name}
                                </a>
                            </li>
                        );
                    })}
                </ul>
            </li>
        </>
    );
}
