import React, { useState } from 'react';

export default function Menu({ title, subTitle, menu, to = '/' }) {
    const [openMenu, setOpenMenu] = useState(false);
    const openMenuHandler = () => setOpenMenu(!openMenu);

    return (
        <>
            {title && <li className="header">{title}</li>}
            {subTitle ? (
                <li className={`treeview ${openMenu && 'active menu-open'}`} onClick={openMenuHandler}>
                    <a href="#">
                        {subTitle.icon}
                        <span>{subTitle.name}</span>
                    </a>
                    {menu && (
                        <ul className="treeview-menu">
                            {menu.map((data, index) => {
                                return (
                                    <li key={index}>
                                        <a href="index.html">
                                            {data.icon}
                                            {data.name}
                                        </a>
                                    </li>
                                );
                            })}
                        </ul>
                    )}
                </li>
            ) : (
                <li className={`treeview ${openMenu && 'active menu-open'}`}>
                    <a href="#">
                        {subTitle.icon}
                        <span>{subTitle.name}</span>
                    </a>
                </li>
            )}
        </>
    );
}
